select DISTINCT b.id,b.name,
(case when g.id is null or g.id = '' then 0 else 1 end) as iscollection,
b.collection_unit as collectionUnit,b.description,b.is_high_quality as isHighQuality, b.collection_level as collectionLevel, b.collections_category as collectionsCategory,c.name as categoryName, b.click_counts as clickCounts,b.collected_counts as collectedCounts, (select f_audio from mip_open_culturalrelic_info where id = b.id) as fAudio,b.f_video as fVideo,b.picture_ids as pictureIds, d.name as museumName, f.name as yearName from mip_open_collection_info b inner join mip_collection_category c on(b.collections_category = c.id) inner join mip_organization d on(b.collection_unit = d.id and d.status='1') left join bj_museum_base_info e on(e.museum_id = d.id and e.del_flag=2) left join mip_year_type f on(f.id = b.year_type)
left join mip_collect g on(g.type=1 and g.uid=#{userid} and g.cid=b.id and g.`status` = '1')
where 1=1
and b.collections_category = #{collectionsCategory}
and e.area = #{city}
and b.year_type = #{year}
and b.name like concat('%',#{name},'%')
and b.is_high_quality = #{isHighQuality}
order by b.sequence desc
limit #{pageSize}
limit #{startRow},#{pageSize}

  			select count(DISTINCT b.id)
  			 from mip_open_collection_info b
				inner join mip_collection_category c on(b.collections_category = c.id)
				inner join mip_organization d on(b.collection_unit = d.id and d.status='1')
				left join bj_museum_base_info e on(e.museum_id = d.id and e.del_flag=2)
  			 where 1=1 

  			 	and b.collections_category = #{collectionsCategory}


  			 	and e.area = #{city}


  			 	and b.year_type = #{year}


  			 	and b.name like concat('%',#{name},'%')


  			 	and b.is_high_quality = #{isHighQuality}

			order by b.sequence desc

  			select c.id,c.name,c.type,c.picture_ids as pictureIds
  			 from mip_collection_category c
			order by c.sequence desc

			limit #{pageSize}


			limit #{startRow},#{pageSize}
