mapper.select:

    select ms.id as id,ms.status as status,ms.begin_time as beginTime,ms.end_time as endTime,ms.editor as editor,ms.user_id as userId,ms.picture_id as pictureId,
    ms.content as content,ms.headline as headline,ms.publish_time as publishTime,ms.exhibition_type as exhibitionType,ms.createTime as createtime,ms.org_id as orgId,
    ms.updated_time as updatedTime,mo.name as orgName,ms.message
    from mip_spreadtrum ms ,mip_organization mo   
    where  ms.org_id = mo.id 

    and ( ms.org_id = #{currentOrg} 


       	or (ms.org_id  in 

       		#{item}

       	 and ms.status in('1', '3'))


    )


		and (ms.headline like '%${key}%' or ms.content like '%${key}%')


		and ms.exhibition_type = #{type} 


		and ms.status = #{status} 


   	 	and ms.publish_time  > '${startTime}'


    	and ms.publish_time < '${endTime}'

	ORDER BY if(ms.status='3',0,1), ms.createTime desc

		limit #{pageSize}


		limit #{startRow},#{pageSize}


    select count(*)
    from mip_spreadtrum ms
    where 1=1

    and ( ms.org_id = #{currentOrg} 


       	or (ms.org_id  in 

       		#{item}

       	 and ms.status in('1', '3'))


    )


		and (ms.headline like '%${key}%' or ms.content like '%${key}%')


		and ms.exhibition_type = #{type} 


		and ms.status = #{status} 


   	 	and ms.publish_time  > '${startTime}'


    	and ms.publish_time < '${endTime}'

mapper.update:
update mip_spreadtrum
set status = #{status,jdbcType=VARCHAR},
editor  = #{userId,jdbcType=VARCHAR},
publish_time  = #{publishTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select s.id,s.picture_id as pictureId,s.headline,s.publish_time as publishTime,s.content as content,s.click_number as clickNumber, s.begin_time as beginTime,s.end_time as endTime,s.viPCUrl as openTime,s.viMoveUrl as openAddress, s.exhibition_type as exhibitionType,g.name as orgName from mip_spreadtrum s,mip_organization g where s.org_id = g.id and s.status = 1
order by ${orderCondition} desc
limit #{pageSize}
limit #{startRow},#{pageSize}

select g.name as museumName,ma.name as area,s.viName as viName,s.viIntro as viIntro,s.id, s.viPCUrl as viPCUrl,s.viMoveUrl as viMoveUrl,s.viUnit as viAddress,s.picture_id as pictureId from mip_virtual_exibition_hall s left join mip_organization g on s.org_id = g.id left join bj_museum_base_info b on b.museum_id = g.id left join mip_area ma on ma.id = b.area where 1=1 and s.status = 1 and b.del_flag = 2 order by publish_time desc
limit #{pageSize}
limit #{startRow},#{pageSize}

  		select s.id,s.picture_id as pictureId,s.headline,s.publish_time as publishTime,s.click_number as clickNumber,b.is_free as isFree,g.latitude as latitude, g.longitude as longitude,
  			s.begin_time as beginTime,s.end_time as endTime,s.viPCUrl as openTime,s.viMoveUrl as openAddress,s.content,s.exhibition_type as exhibitionType,g.name as orgName,s.org_id as orgId
  			from mip_spreadtrum s,mip_organization g ,bj_museum_base_info b
  			where s.org_id = g.id and b.museum_id = g.id and b.del_flag = 2

				and s.org_id = #{orgId}


				and s.id = #{id}

select g.name as museumName,ma.name as area,s.viName as viName,s.viIntro as viIntro,s.id,'' as location from mip_virtual_exibition_hall s left join mip_organization g on s.org_id = g.id left join bj_museum_base_info b on b.museum_id = g.id left join mip_area ma on ma.id = b.area where 1=1 and s.status = 1 and b.del_flag = 2 order by publish_time desc
and s.org_id = #{orgId}
and s.id = #{id}

  		select p.att_name as attName,p.att_path as attPath,p.att_file_type as attFileType from mip_attachment p
  		where 1=1

  			and p.att_fk_id = #{attFkId}


  			and p.att_id = #{attId}


			limit #{pageSize}


			limit #{startRow},#{pageSize}

select count(1) from mip_attachment p
where 1=1

	and p.att_fk_id = #{attFkId}


	and p.att_id = #{attId}

  			select s.id,s.picture_id as pictureId,s.headline,s.exhibition_type as exhibitionType,
  			s.begin_time as beginTime,s.end_time as endTime,s.viPCUrl as openTime,s.viMoveUrl as openAddress			
  			from mip_spreadtrum s,mip_organization g ,bj_museum_base_info b
  			where s.org_id = g.id and b.museum_id = g.id and b.del_flag = 2 and s.status = 1

				and (s.headline like '%${key}%' or s.content like '%${key}%')


  				and b.area = #{area}

  			order by s.click_number desc

				limit #{pageSize}


				limit #{startRow},#{pageSize}

select count(1) from mip_spreadtrum s,mip_organization g ,bj_museum_base_info b where s.org_id = g.id and b.museum_id = g.id and b.del_flag = 2 and s.status = 1
and (s.headline like '%${key}%' or s.content like '%${key}%')
and b.area = #{area}
